$(document).ready(function(){$(".fancybox").fancybox({type:"iframe",titlePosition:"inside",transitionIn:"fade",transitionOut:"fade",beforeShow:function(){var t=this.element.data("id");this.title=this.title+"<a class ='view_lesson' href='/featured/"+t+"'> Take Notes & Watch</a>"}});var t=function(t){$.ajax("/views",{dataType:"json",data:{video_id:t},method:"POST",beforeSend:function(t){t.setRequestHeader("X-CSRF-Token",$('meta[name="csrf-token"]').attr("content"))}})};$(".thumb").click(function(){$(this).addClass("active");{var e=$(this).parent().data("id");setTimeout(function(){t(e)},5e3)}})});